function generateMockRecords(count = 100000) {
  const records = [];

  for (let i = 1; i <= count; i++) {
    const id = i;
    const customerName = `Customer ${i}`;
    const email = `customer${i}@example.com`;
    const items = Math.floor(Math.random() * 10) + 1;
    const subtotal = parseFloat((Math.random() * 100 + 20).toFixed(2));
    const delivery = parseFloat((Math.random() * 10 + 5).toFixed(2));
    const totalCost = parseFloat((subtotal + delivery).toFixed(2));

    records.push({
      id,
      customerName,
      email,
      items,
      subtotal,
      delivery,
      totalCost
    });
  }

  return records;
}

return generateMockRecords();
