const data = queries.getLicenses.data;
const currentDate = moment().startOf("day");

function segregateLicensesByValidity(data) {
  const activeLicenses = [];
  const inactiveLicenses = [];
  const allLicenses = [];

  data.forEach((license) => {
    const validTillDate = moment(license.valid_till, "DD/MM/YYYY");

    if (validTillDate.isSameOrAfter(currentDate)) {
      license.status = 'active';
      activeLicenses.push(license);
    } else {
      license.status = 'inactive';
      inactiveLicenses.push(license);
    }

    allLicenses.push(license);
  });

  return { active: activeLicenses, inactive: inactiveLicenses, all: allLicenses };
}

const segregatedData = segregateLicensesByValidity(data);
return segregatedData;
