// Array to hold generated sentences
const sentences = [];

// Loop to generate specified count of sentences
for (let i = 0; i < parameters.count; i++) {
  // Generate a random count of words for this sentence (between 8 and 24)
  let randomCount = Math.floor(Math.random() * 17) + 8;

  // Generate words using the query with the random count
  await queries.generateWords.run({ count: randomCount });
  let sentence = queries.generateWords.getData();

  // Capitalize the first letter of the sentence and make the rest lowercase
  sentence = sentence[0].toUpperCase() + sentence.slice(1).toLowerCase();

  // Append punctuation to the end of the sentence
  sentence += ".";

  // Append the generated sentence to the sentences array
  sentences.push(sentence);
}

// Join the generated sentences into a single string separated by spaces and return
return sentences.join(" ");

