import React, { useState, useEffect } from "https://cdn.skypack.dev/react";
import ReactDOM from "https://cdn.skypack.dev/react-dom";


// Dance class options
const danceClassOptions = [
 { value: "bharatanaatiyam", label: "Bharatanaatiyam" },
 { value: "mohiniyaatam", label: "Mohiniyaatam" },
 { value: "thiruvathirakkali", label: "Thiruvathirakkali" },
 { value: "semiclassical", label: "Semi-Classical" },
];


// Style objects
const containerFormStyle = {
 display: "flex",
 justifyContent: "center",
 alignItems: "center",
 backgroundColor: "#F2EDDD",
 flexDirection: "column",
 padding: "80px 0px",
};
const formTitleStyle = {
 textAlign: "center",
 fontSize: "60px",
 color: "#800000", // Updated to maroon
 marginBottom: "2.5rem",
};
const formGroupStyle = {
 display: "flex",
 flexDirection: "column",
 marginBottom: "1.5rem",
};
const labelStyle = {
 fontSize: "1.1rem",
 marginBottom: "0.7rem",
 color: "#800000", // Updated to maroon
 fontWeight: "500",
};
const inputStyle = {
 padding: "0.75rem 1rem",
 fontSize: "1rem",
 border: "1px solid #A52A2A", // Updated to brown
 borderRadius: "6px",
 backgroundColor: "#fff",
 width: "375px",
 transition: "border-color 0.2s",
};
const inputFocusStyle = {
 outline: "none",
 borderColor: "#A52A2A", // Updated to brown
 boxShadow: "0 0 0 2px rgba(165, 42, 42, 0.2)", // Optional: subtle glow
};
const selectStyle = {
 ...inputStyle,
 padding: "0.75rem 0.8rem",
};
const submitBtnStyle = {
 backgroundColor: "#800000", // Updated to maroon
 color: "#fff",
 padding: "0.9rem 2rem",
 fontSize: "1.1rem",
 border: "none",
 borderRadius: "6px",
 cursor: "pointer",
 transition: "background-color 0.3s",
 width: "220px",
 margin: "40px auto 0 auto",
 fontWeight: "500",
};
const submitBtnHoverStyle = {
 backgroundColor: "#580000", // Darker maroon on hover
};
const alertStyle = {
 position: "relative",
 padding: "0.9rem 1.25rem",
 marginBottom: "1.5rem",
 border: "1px solid transparent",
 borderRadius: "0.4rem",
 color: "#155724",
 backgroundColor: "#d4edda",
 borderColor: "#c3e6cb",
 fontSize: "1.05rem",
};
const btnCloseStyle = {
 position: "absolute",
 top: "0",
 right: "0",
 padding: "0.75rem 1.25rem",
 color: "inherit",
 backgroundColor: "transparent",
 border: "0",
 cursor: "pointer",
 fontSize: "1.5rem",
 fontWeight: "700",
 lineHeight: "1",
 textShadow: "0 1px 0 #fff",
 opacity: ".5",
};
const formContainerStyle = {
 backgroundColor: "#FAF9F6", // Light beige
 padding: "2.5rem 3rem",
 width: "860px",
 margin: "0 auto",
 borderRadius: "12px",
 boxShadow: "0 6px 16px rgba(0, 0, 0, 0.08)",
};
const rowStyle = {
 display: "flex",
 justifyContent: "space-between",
 flexWrap: "wrap",
 gap: "1.5rem",
 marginBottom: "1.5rem",
};
const fieldGroupStyle = {
 flex: "1 1 45%",
 minWidth: "320px",
};


const EnquiryForm = ({ data, updateData }) => {
 const [show, setShow] = useState(false);
 const [width, setWidth] = useState(window.innerWidth);


 useEffect(() => {
 const handleResize = () => setWidth(window.innerWidth);
 window.addEventListener("resize", handleResize);
 return () => window.removeEventListener("resize", handleResize);
 }, []);


 const formData = data || {
 fullName: "",
 age: "",
 email: "",
 phone: "",
 enrollmentDate: "",
 danceClass: "",
 };


 const handleInputChange = (e) => {
 const { name, value } = e.target;
 updateData({ ...formData, [name]: value });
 };


 const handleSubmit = (e) => {
 e.preventDefault();
 setShow(true);
 updateData({
 fullName: "",
 age: "",
 email: "",
 phone: "",
 enrollmentDate: "",
 danceClass: "",
 });
 };


 // Responsive adjustments
 const responsiveFormContainerStyle = {
 ...formContainerStyle,
 width: width < 992 ? (width < 768 ? "90%" : "600px") : "860px",
 padding: width < 992 ? (width < 768 ? "1.5rem" : "2rem") : "2.5rem 3rem",
 };
 const responsiveFormTitleStyle = {
 ...formTitleStyle,
 fontSize: width < 992 ? (width < 768 ? "40px" : "50px") : "60px",
 };
 const responsiveSubmitBtnStyle = {
 ...submitBtnStyle,
 width: width < 992 ? (width < 768 ? "180px" : "200px") : "220px",
 padding: width < 992 ? (width < 768 ? "0.8rem 1.6rem" : "0.8rem 1.8rem") : "0.9rem 2rem",
 };
 const responsiveInputStyle = {
 ...inputStyle,
 width: width < 768 ? "100%" : "100%",
 minWidth: "280px",
 };
 const responsiveSelectStyle = {
 ...selectStyle,
 width: width < 768 ? "100%" : "100%",
 minWidth: "280px",
 };


 return (
 <div style={containerFormStyle}>
 <h1 style={responsiveFormTitleStyle}>Enquiry Form</h1>
 <div style={responsiveFormContainerStyle}>
 <form
 onSubmit={handleSubmit}
 style={{ display: "flex", flexDirection: "column" }}
 >
 {show && (
 <div style={alertStyle}>
 Form Submitted Successfully
 <button
 type="button"
 style={btnCloseStyle}
 onClick={() => setShow(false)}
 >
 &times;
 </button>
 </div>
 )}
 <div style={formGroupStyle}>
 <label style={labelStyle}>Full Name</label>
 <input
 type="text"
 name="fullName"
 value={formData.fullName}
 onChange={handleInputChange}
 required
 style={responsiveInputStyle}
 onFocus={(e) => (e.target.style = { ...responsiveInputStyle, ...inputFocusStyle })}
 onBlur={(e) => (e.target.style = responsiveInputStyle)}
 />
 </div>
 <div style={formGroupStyle}>
 <label style={labelStyle}>Email</label>
 <input
 type="email"
 name="email"
 value={formData.email}
 onChange={handleInputChange}
 required
 style={responsiveInputStyle}
 onFocus={(e) => (e.target.style = { ...responsiveInputStyle, ...inputFocusStyle })}
 onBlur={(e) => (e.target.style = responsiveInputStyle)}
 />
 </div>
 <div style={rowStyle}>
 <div style={{ ...formGroupStyle, ...fieldGroupStyle }}>
 <label style={labelStyle}>Age</label>
 <input
 type="number"
 name="age"
 value={formData.age}
 onChange={handleInputChange}
 required
 style={responsiveInputStyle}
 onFocus={(e) => (e.target.style = { ...responsiveInputStyle, ...inputFocusStyle })}
 onBlur={(e) => (e.target.style = responsiveInputStyle)}
 />
 </div>
 <div style={{ ...formGroupStyle, ...fieldGroupStyle }}>
 <label style={labelStyle}>Phone</label>
 <input
 type="tel"
 name="phone"
 value={formData.phone}
 onChange={handleInputChange}
 required
 style={responsiveInputStyle}
 onFocus={(e) => (e.target.style = { ...responsiveInputStyle, ...inputFocusStyle })}
 onBlur={(e) => (e.target.style = responsiveInputStyle)}
 />
 </div>
 </div>
 <div style={rowStyle}>
 <div style={{ ...formGroupStyle, ...fieldGroupStyle }}>
 <label style={labelStyle}>Enrollment Date</label>
 <input
 type="date"
 name="enrollmentDate"
 value={formData.enrollmentDate}
 onChange={handleInputChange}
 style={responsiveInputStyle}
 onFocus={(e) => (e.target.style = { ...responsiveInputStyle, ...inputFocusStyle })}
 onBlur={(e) => (e.target.style = responsiveInputStyle)}
 />
 </div>
 <div style={{ ...formGroupStyle, ...fieldGroupStyle }}>
 <label style={labelStyle}>Dance Class</label>
 <select
 name="danceClass"
 value={formData.danceClass}
 onChange={handleInputChange}
 required
 style={responsiveSelectStyle}
 onFocus={(e) => (e.target.style = { ...responsiveSelectStyle, ...inputFocusStyle })}
 onBlur={(e) => (e.target.style = responsiveSelectStyle)}
 >
 <option value="">Select Dance Class</option>
 {danceClassOptions.map((opt) => (
 <option key={opt.value} value={opt.value}>{opt.label}</option>
 ))}
 </select>
 </div>
 </div>
 <button type="submit" style={responsiveSubmitBtnStyle}>
 Submit
 </button>
 </form>
 </div>
 </div>
 );
};


const ConnectedComponent = Tooljet.connectComponent(EnquiryForm);
ReactDOM.render(<ConnectedComponent />, document.body);
