const validActions = [
  { name: 'open' },
  { name: 'close' },
  { name: 'open' },
  { name: 'close' }
];

function wait(ms) {
  return new Promise(res => setTimeout(res, ms));
}

async function runValidModalLegacy1Tests() {
  for (const action of validActions) {
    const fn = components.modallegacy1?.[action.name];
    if (typeof fn === 'function') {
      try {
        await fn();
        actions.showAlert('success', `✅ ${action.name}() called successfully`);
      } catch (e) {
        actions.showAlert('error', `❌ ${action.name}() failed: ${e.message}`);
      }
    } else {
      actions.showAlert('warning', `⚠️ Function ${action.name} not found on modallegacy1`);
    }
    await wait(1000);
  }
  return '✅ All valid actions executed for modallegacy1.';
}

return runValidModalLegacy1Tests();

const invalidTests = [
  { name: 'open', args: [true] },
  { name: 'close', args: ['wrong'] },
  { name: 'open', args: [{ force: true }] },
  { name: 'close', args: [123] },
  { name: 'toggle', args: [] } // doesn't exist
];

function wait(ms) {
  return new Promise(res => setTimeout(res, ms));
}

async function runInvalidModalLegacy1Tests() {
  for (const test of invalidTests) {
    const fn = components.modallegacy1?.[test.name];
    if (typeof fn === 'function') {
      try {
        await fn(...test.args);
        actions.showAlert('error', `❌ ${test.name}(${JSON.stringify(test.args)}) did NOT throw`);
      } catch (e) {
        actions.showAlert('info', `✅ ${test.name}(${JSON.stringify(test.args)}) threw as expected`);
      }
    } else {
      actions.showAlert('info', `✅ Function ${test.name} does not exist as expected`);
    }
    await wait(1000);
  }
  return '✅ All invalid test cases for modallegacy1 complete.';
}

return runInvalidModalLegacy1Tests();
